SELECT * FROM CLIENTES;
SELECT C.ID_COMPRA, C.FECHA_COMPRA, C.ID_CLIENTE, C.ID_FORMA_DE_PAGO, C.NUMERO_DE_FACTURA, SUM(M.PRECIO*DC.CANTIDAD) AS MONTO_TOTAL FROM COMPRAS C INNER JOIN DETALLES_COMPRA DC ON C.ID_COMPRA=DC.ID_COMPRA INNER JOIN MEDICAMENTOS M ON DC.ID_MEDICAMENTO=M.ID_MEDICAMENTO GROUP BY  C.ID_COMPRA, C.FECHA_COMPRA, C.ID_CLIENTE, C.ID_FORMA_DE_PAGO, C.NUMERO_DE_FACTURA;
SELECT ID_FORMA_DE_PAGO, COUNT(*) AS CANTIDAD_COMPRAS FROM COMPRAS GROUP BY ID_FORMA_DE_PAGO;